      it "assigns a newly created but unsaved service_grade as @service_grade" do
        ServiceGrade.stub(:new).with({'these' => 'params'}) { mock_service_grade(:save => false) }
        post :create, :service_grade => {'these' => 'params'}
        assigns(:service_grade).should be(mock_service_grade)
      end

      it "re-renders the 'new' template" do
        ServiceGrade.stub(:new) { mock_service_grade(:save => false) }
        post :create, :service_grade => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested service_grade" do
        ServiceGrade.stub(:find).with("37") { mock_service_grade }
        mock_service_grade.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :service_grade => {'these' => 'params'}
      end

      it "assigns the requested service_grade as @service_grade" do
        ServiceGrade.stub(:find) { mock_service_grade(:update_attributes => true) }
