      it "assigns the service_grade as @service_grade" do
        ServiceGrade.stub(:find) { mock_service_grade(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:service_grade).should be(mock_service_grade)
      end

      it "re-renders the 'edit' template" do
        ServiceGrade.stub(:find) { mock_service_grade(:update_attributes => false) }
        put :update, :id => "1"
